package com.candidxd.justchat.service.impl;import com.candidxd.justchat.dao.BaseDao;import com.candidxd.justchat.service.BaseService;import org.slf4j.Logger;import java.util.List;/** * @author yzk * @Title: BaseServiceImpl * @ProjectName demo * @Description: TODO * @date 2018/8/2111:27 */public abstract class BaseServiceImpl<T> implements BaseService<T> {    /**     * 获取logger     *     * @return org.slf4j.Logger     * @author yaozekai     * @date 2018/8/29 13:25     */    public abstract Logger logger();    /**     * 获得Dao     *     * @return com.demo.dao.BaseDao<T>     * @author yaozekai     * @date 2018/8/23 15:01     */    public abstract BaseDao<T> getMainDao();    @Override    public void add(T t) {        logger().info("===执行ADD方法===");        getMainDao().add(t);    }    @Override    public void update(T t) {        logger().info("===执行UPDATE方法===");        getMainDao().update(t);    }    @Override    public List<T> list() {        logger().info("===执行LIST方法===");        return getMainDao().list();    }    @Override    public void delete(T t) {        logger().info("===执行DELETE方法===");        getMainDao().delete(t);    }}