package com.candidxd.justchat.service.impl;import com.candidxd.justchat.bean.Customer;import com.candidxd.justchat.bean.CustomerStatus;import com.candidxd.justchat.dao.BaseDao;import com.candidxd.justchat.dao.CustomerStatusDao;import com.candidxd.justchat.service.CustomerService;import com.candidxd.justchat.service.CustomerStatusService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author yzk * @Title: CustomerServiceImpl * @ProjectName demo * @Description: TODO * @date 2018/8/2111:24 */@Servicepublic class CustomerStatusServiceImpl extends BaseServiceImpl<CustomerStatus> implements CustomerStatusService {    @Autowired    private CustomerStatusDao customerStatusDao;    @Override    public Logger logger() {        return LoggerFactory.getLogger(CustomerStatusServiceImpl.class);    }    @Override    public BaseDao<CustomerStatus> getMainDao() {        return customerStatusDao;    }    @Override    public List<CustomerStatus> matching(CustomerStatus customerStatus) {        return customerStatusDao.matching(customerStatus);    }    @Override    public CustomerStatus getOne(CustomerStatus customerStatus) {        return customerStatusDao.getOne(customerStatus);    }}