package com.candidxd.justchat.util;import java.io.File;import java.io.IOException;import java.nio.file.Files;import java.nio.file.Path;import java.nio.file.Paths;import java.nio.file.StandardCopyOption;/** * @author yzk * @Title: FileUtil * @ProjectName demo * @Description: 文件处理 * @date 2018/9/310:30 */public class FileUtil {    /**     * 如果目录不存在，创建目录     *     * @param dir : 目录     * @return void     * @author yzk     * @date 2018/9/3 14:04     */    public static void createDir(String dir) {        File file = new File(dir);        if (!file.exists()) {            boolean x = file.mkdir();            System.out.println(x);        }    }    /**     * 获取项目中静态资源路径     *     * @param object : object     * @return java.lang.String     * @author yzk     * @date 2018/9/3 14:05     */    public static String getStaticPath(Object object) {        String paths = object.getClass().getResource("").toString();        return paths.substring(paths.indexOf("/") + 1, paths.indexOf("com")) + "static/";    }    /**     * 文件拷贝     *     * @param fromSrc : 源文件路径     * @param toSrc   : 目标文件路径     * @return void     * @author yzk     * @date 2018/9/3 14:05     */    public static void fileCopy(String fromSrc, String toSrc) throws IOException {        Path fromPath = Paths.get(fromSrc);        Path toPath = Paths.get(toSrc);        Files.copy(fromPath, toPath, StandardCopyOption.REPLACE_EXISTING);    }}