package com.candidxd.justchat.util;import com.candidxd.justchat.enums.ReturnEnum;/** * @author yzk * @Title: JsonUtil * @ProjectName demo * @Description: TODO * @date 2018/8/229:39 */public class JsonUtil {    public static String getData(String state, String content) {        return "{\"state\":\"" + state + "\",\"content\":\"" + content + "\"}";    }    public static String getDataJson(String state, String content) {        return "{\"state\":\"" + state + "\",\"content\":" + content + "}";    }    public static String getDataPage(String state, String content, String page) {        return "{\"state\":\"" + state + "\",\"page\":\"" + page + "\",\"content\":" + content + "}";    }}